import React, {Component,useState, useEffect} from 'react';
import ConversationSearch from './ConversationSearch';
import ConversationListItem from './ConversationListItem';
import Toolbar from './Toolbar';
import ToolbarButton from './ToolbarButton';
import axios from 'axios';
import { apiGet } from './api';
import './Style/ConversationList.css';
import usersService from '../users/UsersService';
import authService from '../api-authorization/AuthorizeService'

export class ConversationList2 extends Component {
    static displayName = ConversationList2.name;

    constructor(props) {
        super(props);
        this.state = { conversations: [], loading: true };
    }
    handleSubmit = (event) => {
        event.preventDefault();
        const form = event.target[0].value
        alert(form);
        // setContactInfo({ FirstName: "", SecondName: "", Age: "", Nationality: "", EmailAdress: ""}); //reset form values after submit

    };
    componentDidMount() {
        this.getConversations();
    }

    static renderForecastsTable(conversations) {
        return (
            <div className="conversation-list">
                <Toolbar
                    title="Messenger"
                    leftItems={[
                        <ToolbarButton key="cog" icon="ion-ios-cog" />
                    ]}
                    rightItems={[
                        <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
                    ]}
                />
                <ConversationSearch />
                {
                    conversations.map(conversation =>

                        <div className="conversation-list-item" key={conversation.userid + conversation.userName} onClick={this.handleSubmit}>
                            <img className="conversation-photo" src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-chat/ava3.webp" alt="conversation" />
                            <div className="conversation-info" >
                                <h1 className="conversation-title">{conversation.userName}</h1>
                                <p className="conversation-snippet">Offline</p>
                            </div>
                        </div>
                )
                }

            </div>
        );
    }
    render() {
        let contents = this.state.loading
            ? <p><em>Loading...</em></p>
            : ConversationList2.renderForecastsTable(this.state.conversations);

        return (
            <div>
                {contents}
            </div>
        );
    }

    async getConversations() {
        const token = await authService.getAccessToken();
        const response = await fetch('api/Messages/GetUser', {
            headers: !token ? {} : { 'Authorization': `Bearer ${token}` }
        });
        const data = await response.json();
        this.setState({ conversations: data, loading: false });
    }
}
