import React, { Component } from 'react';
import {
    MDBCol,MDBContainer,MDBRow,MDBCard,MDBCardText,MDBCardBody,MDBCardImage,MDBBtn,
    MDBBreadcrumb,MDBBreadcrumbItem,MDBProgress,MDBProgressBar,MDBIcon,MDBListGroup, MDBListGroupItem} from 'mdb-react-ui-kit';
import usersService from '../users/UsersService';
    export class FetchProfil
    extends Component {
        static displayName = FetchProfil.name;

  constructor(props) {
    super(props);
      this.state = { user: null, loading: true };




      this.userId = "06d10224-487a-45b5-bec6-d0fac7e07903";

        }

        componentDidMount() {
            this.retrieveFormData();
        }


        renderUserForm(user) {

            return (
                <div>

                    <p>{user.id}</p>  
                    <p>{user.rowVersiond}</p > 

                    <p>{user.firstName}</p > 
                    <p>{user.lastName}</p > 
                    <p>{user.email}</p>
                    <p>{user.password}</p >

                    <p>{user.role}</p >
                    <p>{user.approved}</p > </div>

		);
        }

  render() {
    let contents = this.state.loading
        ? <p><em>Loading...</em></p>
        : this.renderUserForm(this.state.user)


    return (
      <div>
        <h1 id="tabelLabel" >Weather forecast</h1>
        <p>This component demonstrates fetching data from the server.</p>
            {contents}
      </div>
    );
  }


        async retrieveFormData() {
            const data = await usersService.getUserprofil(this.userId);
            this.setState({ user: data, loading: false });
            console.log(data)
        }
}
