import React, {useState, useEffect} from 'react';
import ConversationSearch from './ConversationSearch';
import ConversationListItem from './ConversationListItem';
import Toolbar from './Toolbar';
import ToolbarButton from './ToolbarButton';
import axios from 'axios';

import './Style/ConversationList.css';
import usersService from '../users/UsersService';
import { apiGet } from './api';
    export class ConversationList extends Component {
        constructor(props) {
            super(props);
            this.state = { users: [], conversations: [], loading: true };
        }


componentDidMount() {
    this.getConversations();
}



        async  getConversations () {
    // setConversations(usersService.getUserChat());

       // let x = usersService.getUserChat();
        const newItem = apiGet("Messages/GetUser");
        if (newItem.errText) {
            alert(newItem.errText);
        }
        setConversations(newItem);
    }

        render() {
            let contents = this.state.loading
                ? <p><em>Loading...</em></p>
                : FetchData.renderForecastsTable(this.state.forecasts);

            return (
                <div className="conversation-list">
                    <Toolbar
                        title="Messenger"
                        leftItems={[
                            <ToolbarButton key="cog" icon="ion-ios-cog" />
                        ]}
                        rightItems={[
                            <ToolbarButton key="add" icon="ion-ios-add-circle-outline" />
                        ]}
                    />
                    <ConversationSearch />
                    {

                        conversations.map(conversation =>
                            <ConversationListItem
                                key={conversation.Userid}
                                data={conversation}
                            />
                        )
                    }
                </div>
            );
        }

}
